package com.merits.modules.base.site.components;

//IMPORTS -------------------------------------------------------------------------

import com.merits.modules.base.site.components.definitions.PageHeaderDefinition;
import com.weezzi.webserver.simel.AttributeMapper;
import com.weezzi.webserver.simel.Expression;


/**
 * <b>Title: </b> PageHeader<br/>
 * <b>Description: </b> <br/>
 *
 * @author Auto-generated by Weezzi Site Builder
 * @version 1.0
 */
public class PageHeader extends PageHeaderDefinition {


	// CONSTANTS ---------------------------------------------------------------

	
	// INITIALIZATION ----------------------------------------------------------
	
	
	public void customComponentInitialization() {
		addGlobalStyle("Merits");
		addGlobalScript("Scripts");
	};


	// BROWSER OUTPUT ----------------------------------------------------------


	/**
	 * Generate Start Output
	 * @return String
	 */
	public String getStartOutput() {
		// Local Variables
		Expression expr;
		AttributeMapper map;
		String selectedStyle = getObfuscatedStyle("Selected");
		// Get Expression
		expr = getHTMLExpression("Default");
		map = expr.getAttributeMapper();
		// Add attributes
		if (getContext().getPageName().equals("About")) {
			map.addAttribute("ABOUT_Selected", selectedStyle);
		}
		if (getContext().getPageName().equals("Contacts")) {
			map.addAttribute("CONTACTS_Selected", selectedStyle);
		}
		
		map.addAttribute("IT_LINK", getPageLink(getPageName(), "it"));
		map.addAttribute("EN_LINK", getPageLink(getPageName(), "en"));
		if (getSiteLanguage().equals("it")) {
			map.addAttribute("IT_CLASS", selectedStyle);	
		} else {
			map.addAttribute("EN_CLASS", selectedStyle);	
		}
		// Render
		return expr.render(map);
	}

	
	// PRIVATE ----------------------------------------------------------------


}

