public class Settings extends SettingsDefinition {


	// CONSTANTS ---------------------------------------------------------------

	
	// INITIALIZATION ----------------------------------------------------------
	
	
	public void customComponentInitialization() {
		addScript("Scripts");
	};


	// BROWSER OUTPUT ----------------------------------------------------------


	/**
	 * Generate Start Output
	 * @return String
	 */
	public String getStartOutput() {
		// Local Variables
		Expression expr;
		AttributeMapper map;
		VolunteersDAO volunteer;
		// Get Expression
		expr = getHTMLExpression("Default");
		map = expr.getAttributeMapper();
		// Add attributes
		switch(MeritsUtils.getUserType(getContext())) {
			case Volunteer:
				map.addAttribute("BACK_LINK", getPageLink("Volunteers_Home"));
				break;
			case Shop:
				map.addAttribute("BACK_LINK", getPageLink("Shops_Home"));
				break;
			case Cashier:
				map.addAttribute("BACK_LINK", getPageLink("Cashiers_Home"));
				break;
		}
		map.addAttribute("LANG_EN", (getSiteLanguage().equals("en")?getObfuscatedStyle("ConfigLanguageSelected"):getObfuscatedStyle("ConfigLanguage")));
		map.addAttribute("LANG_IT", (getSiteLanguage().equals("it")?getObfuscatedStyle("ConfigLanguageSelected"):getObfuscatedStyle("ConfigLanguage")));
		// Render
		return expr.render(map);
	}

	
	// PRIVATE ----------------------------------------------------------------


}

